<?php// The connection info for the DB	$GLOBALS['host'] = "justlight-greenbuilding.cnmcf3jgnh2u.us-west-1.rds.amazonaws.com:3306";$GLOBALS['username'] = "GreenBuilding";$GLOBALS['password'] = "Green2018B";$GLOBALS['dbname'] = "green_house_energy_solutions";$GLOBALS['location']='http://54.183.64.183/php/webservice.php';$GLOBALS['uri']='http://54.183.64.183';// The Database connection moduleclass DBComponent {    var $_dbConn = 0;    var $_queryResource = 0;    	// Connect to database    function Connect_DB()    {        $dbConn = mysqli_connect($GLOBALS['host'], $GLOBALS['username'], $GLOBALS['password'], $GLOBALS['dbname']);        if (! $dbConn)		{			echo "Error: Unable to connect to MySQL." . PHP_EOL;			echo "Debugging errno: " . mysqli_connect_errno() . PHP_EOL;			echo "Debugging error: " . mysqli_connect_error() . PHP_EOL;			exit;			}        $this->_dbConn = $dbConn;        return true;    }    	// Disconnect to database	function Disconnect_DB()	{		mysqli_close($this->_dbConn);	}		function GetDBConn()	{		return $this->_dbConn;	}		// Query to the database. It will check the connection status before the query. If there is no available connection, it will reconnect for once.    function Query($sql)    {		// If the connection is unavailable, try reconnect once.		if (!mysqli_ping($this->_dbConn)) 			Connect_DB();		if (!mysqli_ping($this->_dbConn)) 		{			printf ("Error: %s\n", mysqli_error($this->_dbConn));			exit;		}				if (! $queryResource = $this->_dbConn->query($sql))			die ("MySQL Query Error");					$this->_queryResource = $queryResource;		return $queryResource;  	    }    	// Get the total row numbers from the previous select statement    function Get_num_rows()    {        return $this->_queryResource->num_rows;    }		// Get the total affected row number	function Get_affected_row()	{		return $this->_dbConn->affected_rows;		}	}?>